mapper.insert:
INSERT INTO loginlock (member_idx, compHistory_idx, loginLock_sDate, loginLock_eDate)
VALUES (
    (SELECT comp_memberIdx FROM comp WHERE comp_idx = #{comp_idx}),
    (SELECT compHistory_idx FROM comphistory WHERE comp_idx = #{comp_idx}),
    #{loginLock_sDate},
    #{loginLock_eDate}
);
