mapper.select:
SELECT *
FROM photo p
INNER JOIN member m ON m.member_idx = #{param1}
	WHERE photo_type = 4 AND m.member_idx = p.photo_idfNum
ORDER BY p.photo_id
LIMIT 1;

SELECT COUNT(*) AS likeCount
FROM recommend
WHERE board_id = #{param1} AND rec_type = 1;

SELECT COUNT(*) AS hateCount
FROM recommend
WHERE board_id = #{param1} AND rec_type = 2;
